void SysTick_IntArm(uint32_t period, uint32_t priority){
  uint32_t value;
  SysTick->CTRL = 0x00;
  SysTick ->LOAD = period-1;
  value = priority << 30;
  SCB ->SHP[1] = SCB->SHP[1]&(~0xC0000000);
  SysTick->VAL = 0;
  SysTick->CTRL = 0x00000007;
}
